import React, { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faFacebook,
  faTwitter,
  faInstagram,
  faWhatsapp,
} from "@fortawesome/free-brands-svg-icons";

const Footer = () => {
  const [showFooter, setShowFooter] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      setShowFooter(scrollY > 200); // Adjust this value based on when you want the footer to appear
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <footer style={{ ...footerStyle, display: showFooter ? "block" : "none" }}>
      <div style={iconContainer}>
        <a
          href="https://www.facebook.com/yourcompany"
          target="_blank"
          rel="noopener noreferrer"
        >
          <FontAwesomeIcon icon={faFacebook} style={iconStyle("#1877F2")} />
        </a>
        <a
          href="https://www.twitter.com/yourcompany"
          target="_blank"
          rel="noopener noreferrer"
        >
          <FontAwesomeIcon icon={faTwitter} style={iconStyle("#1DA1F2")} />
        </a>
        <a
          href="https://www.instagram.com/yourcompany"
          target="_blank"
          rel="noopener noreferrer"
        >
          <FontAwesomeIcon icon={faInstagram} style={iconStyle("#E1306C")} />
        </a>
        <a
          href="https://api.whatsapp.com/send?phone=1234567890"
          target="_blank"
          rel="noopener noreferrer"
        >
          <FontAwesomeIcon icon={faWhatsapp} style={iconStyle("#25D366")} />
        </a>
      </div>
      <p>
        Â© {new Date().getFullYear()} Pizza Restaurants. All rights reserved.
      </p>
    </footer>
  );
};

const footerStyle = {
  backgroundColor: "#D7BDE2",
  textAlign: "center",
  padding: "10px",
  position: "fixed",
  left: 0,
  bottom: 0,
  width: "100%",
  display: "",
};

const iconContainer = {
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
};

const iconStyle = (color) => ({
  fontSize: "24px",
  color: color,
  margin: "0 5px",
});

export default Footer;
